from tblphadrug p,tbldrugtype d,tblstodrug s,tbldf f,tblparam m where 
p.drugid=s.drugid(+) and s.typeid=d.typeid(+) and s.dosageId=f.dosageId(+) and p.useableid=m.paramid(+)



	and s.drugId like concat(concat('%',#{drugId,jdbcType=NUMERIC}),'%')


	and s.typeId =#{typeId,jdbcType=NUMERIC}


	and p.useableid =#{useableId,jdbcType=NUMERIC}

	
		and s.pinyincode like
		concat(concat('%',#{pinyinCode}),'%')
	
	
		and s.drugname like concat(concat('%',#{drugName}),'%')
	
	
		and p.useableid=#{useableId}
	
	
		)where r>=#{pageNum}*#{pageLimit}-#{pageLimit}+1 and
		r<=#{pageNum}*#{pageLimit}
	
	
		)

